import {
  CardDef,
  StringField,
  contains,
  field,
} from 'https://cardstack.com/base/card-api';

export class User extends CardDef {
  static displayName = 'User';
  @field name = contains(StringField);
  @field email = contains(StringField);
  @field cardTitle = contains(StringField, {
    computeVia: function (this: User) {
      return this.name;
    },
  });
}

import { hash } from '@ember/helper';
import { htmlSafe } from '@ember/template';
import {
  Component,
  field,
  contains,
  StringField,
  FieldDef,
} from 'https://cardstack.com/base/card-api';
import NumberField from 'https://cardstack.com/base/number';
import { ImageSizeField } from 'https://cardstack.com/base/base64-image';
import UrlField from 'https://cardstack.com/base/url';
import { FieldContainer } from '@cardstack/boxel-ui/components';
import { FailureBordered } from '@cardstack/boxel-ui/icons';
import PhotoIcon from '@cardstack/boxel-icons/photo';

const setBackgroundImage = (backgroundURL: string | null | undefined) => {
  if (!backgroundURL) {
    return;
  }
  return htmlSafe(`background-image: url(${backgroundURL});`);
};

function cssForFeaturedImage({
  imageUrl,
  size,
  height,
  width,
}: {
  imageUrl: string | undefined;
  size: 'actual' | 'contain' | 'cover' | undefined;
  height?: number;
  width?: number;
}) {
  if (!imageUrl) {
    return undefined;
  }

  let css: string[] = [];
  css.push(`background-image: url("${imageUrl}");`);
  if (size && ['contain', 'cover'].includes(size)) {
    css.push(`background-size: ${size};`);
  }
  if (height) {
    css.push(`height: ${height}px;`);
  }
  if (width) {
    css.push(`width: ${width}px`);
  } else {
    css.push(`width: 100%`);
  }
  return htmlSafe(css.join(' '));
}

export default class FeaturedImageField extends FieldDef {
  static displayName = 'Featured Image';
  static icon = PhotoIcon;
  @field imageUrl = contains(UrlField);
  @field credit = contains(StringField);
  @field caption = contains(StringField);
  @field altText = contains(StringField);
  @field size = contains(ImageSizeField);
  @field height = contains(NumberField);
  @field width = contains(NumberField);
  static edit = class Edit extends Component<typeof this> {
    get usesActualSize() {
      return this.args.model.size === 'actual' || this.args.model.size == null;
    }

    get backgroundMaskStyle() {
      let css: string[] = [];
      if (this.args.model.height) {
        css.push(`height: ${this.args.model.height}px;`);
      }
      if (this.args.model.width) {
        css.push(`width: ${this.args.model.width}px`);
      }
      return htmlSafe(css.join(' '));
    }

    get needsHeight() {
      return (
        (this.args.model.size === 'contain' ||
          this.args.model.size === 'cover') &&
        !this.args.model.height
      );
    }

    <template>
      <div class='featured-image-edit'>
        {{#if @model.imageUrl}}
          <FieldContainer class='preview-field' @label='Preview' @tag='label'>
            {{#if this.needsHeight}}
              <p class='height-warning'>
                <FailureBordered />
                Can't render current image. Please provide a height when using
                the "contain" or "cover" size.
              </p>
            {{else if this.usesActualSize}}
              <img
                class='image'
                src={{@model.imageUrl}}
                height={{@model.height}}
                width={{@model.width}}
                alt={{@model.altText}}
              />
            {{else}}
              <div
                class='checkerboard-preview-background'
                style={{this.backgroundMaskStyle}}
              >
                <div
                  class='image'
                  role='img'
                  aria-label={{@model.altText}}
                  style={{cssForFeaturedImage
                    (hash
                      imageUrl=@model.imageUrl
                      size=@model.size
                      height=@model.height
                      width=@model.width
                    )
                  }}
                />
              </div>
            {{/if}}
          </FieldContainer>
        {{/if}}
        <FieldContainer @label='Image Url' @tag='label'>
          <@fields.imageUrl />
        </FieldContainer>
        <FieldContainer @label='Alt Text' @tag='label'>
          <@fields.altText />
        </FieldContainer>
        <FieldContainer @label='Caption' @tag='label'>
          <@fields.caption />
        </FieldContainer>
        <FieldContainer @label='Credit' @tag='label'>
          <@fields.credit />
        </FieldContainer>
        <FieldContainer @label='Size' @tag='label'>
          <@fields.size />
        </FieldContainer>
        <FieldContainer @label='Height (px)' @tag='label'>
          <@fields.height />
        </FieldContainer>
        <FieldContainer @label='Width (px)' @tag='label'>
          <@fields.width />
        </FieldContainer>
      </div>

      <style scoped>
        .featured-image-edit {
          display: grid;
          gap: var(--boxel-sp);
          padding: var(--boxel-sp);
          background-color: var(--boxel-100);
          border-radius: var(--boxel-form-control-border-radius);
        }
        .preview-field :deep(.content) {
          display: flex;
          align-items: center;
          justify-content: center;
          width: 100%;
        }
        .preview-field.boxel-field > :deep(.content) > .image {
          flex: unset;
        }
        .image {
          background-repeat: no-repeat;
          background-position: center;
        }
        .checkerboard-preview-background {
          display: flex;
          align-items: center;
          justify-content: center;
          width: 100%;
          border: 1px solid var(--boxel-form-control-border-color);
          border-radius: var(--boxel-form-control-border-radius);
          background-color: white;
          background-size: 24px 24px;
          background-position:
            12px 12px,
            -12px 0,
            0 0,
            0 12px;
          background-image:
            linear-gradient(45deg, var(--boxel-300) 25%, transparent 25%),
            linear-gradient(-45deg, var(--boxel-300) 25%, transparent 25%),
            linear-gradient(45deg, transparent 75%, var(--boxel-300) 75%),
            linear-gradient(-45deg, transparent 75%, var(--boxel-300) 75%);
          overflow: hidden;
        }
        .height-warning {
          display: flex;
          align-items: center;
          margin: 0;
          color: var(--boxel-error-200);
          font: 500 var(--boxel-font-sm);
          letter-spacing: var(--boxel-lsp-xs);
        }
        .height-warning svg {
          min-width: 20px;
          margin-right: var(--boxel-sp-xxs);
        }
      </style>
    </template>
  };

  static atom = class Atom extends Component<typeof this> {
    <template>
      {{#if @model.imageUrl}}
        <div
          class='image'
          role='img'
          alt={{@model.altText}}
          style={{setBackgroundImage @model.imageUrl}}
        />
      {{/if}}
      <style scoped>
        .image {
          width: var(--atom-image-width, auto);
          height: var(--atom-image-height, 100px);
          background-size: var(--atom-image-background-size, contain);
          background-repeat: no-repeat;
          background-position: center;
        }
      </style>
    </template>
  };
  static embedded = class Embedded extends Component<typeof this> {
    get usesActualSize() {
      return this.args.model.size === 'actual' || this.args.model.size == null;
    }
    <template>
      {{#if @model.imageUrl}}
        <figure>
          {{#if this.usesActualSize}}
            <img
              class='image'
              src={{@model.imageUrl}}
              height={{@model.height}}
              width={{@model.width}}
              alt={{@model.altText}}
            />
          {{else}}
            <div
              class='image'
              role='img'
              aria-label={{@model.altText}}
              style={{cssForFeaturedImage
                (hash
                  imageUrl=@model.imageUrl
                  size=@model.size
                  height=@model.height
                  width=@model.width
                )
              }}
            >
            </div>
          {{/if}}
          <figcaption>
            <p class='credit'><small><@fields.credit /></small></p>
            <p class='caption'><@fields.caption /></p>
          </figcaption>
        </figure>
      {{/if}}
      <style scoped>
        figure,
        img {
          max-width: 100%;
        }
        figure,
        p {
          margin: 0;
        }
        figcaption {
          font-size: 0.8125em;
          font-style: italic;
        }
        .image {
          background-repeat: no-repeat;
          background-position: center;
        }
        .credit {
          line-height: 1.1;
        }
        .credit + .caption {
          margin-top: var(--boxel-sp-4xs);
        }
      </style>
    </template>
  };
}

import {
  Component,
  field,
  contains,
  StringField,
  FieldDef,
} from 'https://cardstack.com/base/card-api';
import UrlField from 'https://cardstack.com/base/url';

import {
  BoxelSelect,
  FieldContainer,
  Pill,
} from '@cardstack/boxel-ui/components';

import type IconComponent from '@cardstack/boxel-icons/captions';
import Email from '@cardstack/boxel-icons/mail';
import Link from '@cardstack/boxel-icons/link';
import Phone from '@cardstack/boxel-icons/phone';

export interface ContactLink {
  type: 'email' | 'tel' | 'link' | string;
  label: string;
  icon: typeof IconComponent;
  cta: string;
}

const contactValues: ContactLink[] = [
  {
    type: 'email',
    label: 'Email',
    icon: Email,
    cta: 'Email',
  },
  {
    type: 'tel',
    label: 'Phone',
    icon: Phone,
    cta: 'Contact',
  },
  {
    type: 'link',
    label: 'Other',
    icon: Link,
    cta: 'Connect',
  },
];

export default class ContactLinkField extends FieldDef {
  static displayName = 'Contact Link';
  static values: ContactLink[] = contactValues;
  @field label = contains(StringField);
  @field value = contains(StringField);
  @field url = contains(UrlField, {
    computeVia: function (this: ContactLinkField) {
      switch (this.item?.type) {
        case 'email':
          return `mailto:${this.value}`;
        case 'tel':
          return `tel:${this.value}`;
        default:
          return this.value;
      }
    },
  });
  get items() {
    if (this.constructor && 'values' in this.constructor) {
      return this.constructor.values as ContactLink[];
    }
    return ContactLinkField.values;
  }
  get item() {
    return this.items?.find((val) => val.label === this.label);
  }
  static edit = class Edit extends Component<typeof this> {
    <template>
      <FieldContainer @vertical={{true}} @label='Type' @tag='label'>
        <BoxelSelect
          @options={{this.options}}
          @selected={{this.selectedOption}}
          @onChange={{this.onSelect}}
          @placeholder='Please Select'
          as |item|
        >
          <div>{{item.label}}</div>
        </BoxelSelect>
      </FieldContainer>
      <FieldContainer @vertical={{true}} @label={{this.label}} @tag='label'>
        <@fields.value />
      </FieldContainer>
      <style scoped>
        label + label {
          margin-top: var(--boxel-sp-xs);
        }
      </style>
    </template>

    options = this.args.model.items;

    onSelect = (option: ContactLink) => (this.args.model.label = option.label);

    get selectedOption() {
      return this.options?.find(
        (option) => option.label === this.args.model.label,
      );
    }

    get label() {
      switch (this.selectedOption?.type) {
        case 'email':
          return 'Address';
        case 'tel':
          return 'Number';
        default:
          return 'Link';
      }
    }
  };
  static atom = class Atom extends Component<typeof this> {
    <template>
      {{#if @model.url}}
        <Pill
          @tag='a'
          href={{@model.url}}
          target='_blank'
          rel='noopener noreferrer'
        >
          <span class='boxel-sr-only'><@fields.label /></span>
          <@model.item.icon height='20' width='20' />
        </Pill>
      {{/if}}
      <style scoped>
        a:hover {
          border-color: var(--boxel-dark);
        }
        a:focus:focus-visible {
          outline-color: var(--boxel-highlight);
          outline-offset: -1px;
        }
      </style>
    </template>
  };
  static embedded = class Embedded extends Component<typeof this> {
    <template>
      {{#if @model.url}}
        <Pill
          @tag='a'
          href={{@model.url}}
          target='_blank'
          rel='noopener noreferrer'
        >
          <:iconLeft>
            <@model.item.icon height='20' width='20' />
          </:iconLeft>
          <:default>
            {{@model.item.cta}}
          </:default>
        </Pill>
      {{/if}}
      <style scoped>
        a {
          --pill-gap: var(--boxel-sp-xxxs);
        }
        a:hover {
          border-color: var(--boxel-dark);
        }
        a:focus:focus-visible {
          outline-color: var(--boxel-highlight);
          outline-offset: -1px;
        }
      </style>
    </template>
  };
}

import GlimmerComponent from '@glimmer/component';

import { type CardContext } from 'https://cardstack.com/base/card-api';

import {
  type Query,
  type PrerenderedCardLike,
} from '@cardstack/runtime-common';

interface CardListSignature {
  Args: {
    query: Query;
    realms: string[];
    context?: CardContext;
  };
  Blocks: {
    meta: [card: PrerenderedCardLike];
  };
  Element: HTMLElement;
}
export class CardList extends GlimmerComponent<CardListSignature> {
  <template>
    <ul class='card-list' ...attributes>
      {{#let
        (component @context.prerenderedCardSearchComponent)
        as |PrerenderedCardSearch|
      }}
        <PrerenderedCardSearch
          @query={{@query}}
          @format='embedded'
          @realms={{@realms}}
          @isLive={{true}}
        >
          <:loading>
            Loading...
          </:loading>
          <:response as |cards|>
            {{#each cards key='url' as |card|}}
              <li class='card-list-item'>
                <card.component
                  class='card'
                />
                {{#if (has-block 'meta')}}
                  {{yield card to='meta'}}
                {{/if}}
              </li>
            {{/each}}
          </:response>
        </PrerenderedCardSearch>
      {{/let}}
    </ul>
    <style scoped>
      .card-list {
        display: grid;
        gap: var(--boxel-sp);
        list-style-type: none;
        margin: 0;
        padding: var(--boxel-sp-6xs);
      }
      .card-list-item {
        display: flex;
        flex-wrap: wrap;
        gap: var(--boxel-sp) var(--boxel-sp-lg);
      }
      .card {
        height: auto;
        min-height: var(--embedded-card-min-height, 345px);
        max-width: var(--embedded-card-max-width, 100%);
      }
      .bordered-items > .card-list-item > * {
        border-radius: var(--boxel-border-radius);
        box-shadow: inset 0 0 0 1px var(--boxel-light-500);
      }
    </style>
  </template>
}

import GlimmerComponent from '@glimmer/component';

import { type CardContext } from 'https://cardstack.com/base/card-api';

import { type Query } from '@cardstack/runtime-common';

interface CardsGridSignature {
  Args: {
    query: Query;
    realms: string[];
    selectedView: string;
    context?: CardContext;
  };
  Element: HTMLElement;
}
export class CardsGrid extends GlimmerComponent<CardsGridSignature> {
  <template>
    <ul
      class='cards {{@selectedView}}-view'
      data-test-cards-grid-cards
      ...attributes
    >
      {{#let
        (component @context.prerenderedCardSearchComponent)
        as |PrerenderedCardSearch|
      }}
        <PrerenderedCardSearch
          @query={{@query}}
          @format='fitted'
          @realms={{@realms}}
          @isLive={{true}}
        >
          <:loading>
            Loading...
          </:loading>
          <:response as |cards|>
            {{#each cards key='url' as |card|}}
              <li class='{{@selectedView}}-view-container'>
                <card.component
                  class='card'
                />
              </li>
            {{/each}}
          </:response>
        </PrerenderedCardSearch>
      {{/let}}
    </ul>
    <style scoped>
      .cards {
        --default-grid-view-min-width: 224px;
        --default-grid-view-max-width: 1fr;
        --default-grid-view-height: 360px;
        --default-strip-view-min-width: 49%;
        --default-strip-view-max-width: 1fr;
        --default-strip-view-height: 180px;

        display: grid;
        gap: var(--boxel-sp);
        list-style-type: none;
        margin: 0;
        padding: var(--boxel-sp-6xs);
      }

      .cards.strip-view {
        grid-template-columns: repeat(
          auto-fill,
          minmax(
            var(--strip-view-min-width, var(--default-strip-view-min-width)),
            var(--strip-view-max-width, var(--default-strip-view-max-width))
          )
        );
        grid-auto-rows: var(
          --strip-view-height,
          var(--default-strip-view-height)
        );
      }

      .cards.grid-view {
        grid-template-columns: repeat(
          auto-fill,
          minmax(
            var(--grid-view-min-width, var(--default-grid-view-min-width)),
            var(--grid-view-max-width, var(--default-grid-view-max-width))
          )
        );
        grid-auto-rows: var(
          --grid-view-height,
          var(--default-grid-view-height)
        );
      }

      .card {
        container-name: fitted-card;
        container-type: size;
      }
    </style>
  </template>
}

import { get } from '@ember/object';
import GlimmerComponent from '@glimmer/component';

import { type Sort, baseRealm } from '@cardstack/runtime-common';

import {
  BoxelButton,
  BoxelDropdown,
  Menu as BoxelMenu,
} from '@cardstack/boxel-ui/components';
import { eq, MenuItem } from '@cardstack/boxel-ui/helpers';
import { DropdownArrowFilled } from '@cardstack/boxel-ui/icons';
import ArrowDown from '@cardstack/boxel-icons/arrow-down';
import ArrowUp from '@cardstack/boxel-icons/arrow-up';

export const sortByCardTitleAsc: Sort = [
  {
    on: {
      module: `${baseRealm.url}card-api`,
      name: 'CardDef',
    },
    by: 'cardTitle',
    direction: 'asc',
  },
];

export interface SortOption {
  id: string;
  displayName: string;
  sort: Sort;
}

interface SortMenuSignature {
  Args: {
    options: SortOption[];
    onSort: (option: SortOption) => void;
    selected: SortOption;
  };
  Element: HTMLElement;
}
export class SortMenu extends GlimmerComponent<SortMenuSignature> {
  <template>
    <div class='sort' ...attributes>
      Sort by
      <BoxelDropdown>
        <:trigger as |bindings|>
          <BoxelButton class='sort-trigger' {{bindings}}>
            <span class='sort-trigger-content'>
              {{@selected.displayName}}
              {{#if (eq (get @selected.sort '0.direction') 'desc')}}
                <ArrowDown width='16' height='16' />
              {{else}}
                <ArrowUp width='16' height='16' />
              {{/if}}
            </span>
            <DropdownArrowFilled
              class='sort-trigger-icon'
              width='10'
              height='10'
            />
          </BoxelButton>
        </:trigger>
        <:content as |dd|>
          <BoxelMenu
            class='sort-menu'
            @closeMenu={{dd.close}}
            @items={{this.sortOptions}}
          />
        </:content>
      </BoxelDropdown>
    </div>
    <style scoped>
      .sort {
        display: flex;
        align-items: center;
        gap: 0 var(--boxel-sp-sm);
        text-wrap: nowrap;
      }
      .sort :deep(.ember-basic-dropdown-content-wormhole-origin) {
        position: absolute; /* This prevents layout shift when menu opens */
      }

      .sort-trigger {
        --boxel-button-border-color: var(--boxel-450);
        width: 190px;
        justify-content: space-between;
        gap: var(--boxel-sp-xs);
        padding-right: var(--boxel-sp-xs);
        padding-left: var(--boxel-sp-xs);
        border-radius: var(--boxel-border-radius);
      }
      .sort-trigger[aria-expanded='true'] .sort-dropdown-icon {
        transform: scaleY(-1);
      }
      .sort-trigger-content {
        display: inline-flex;
        align-items: center;
        gap: var(--boxel-sp-xxxs);
      }
      .sort-trigger-icon {
        flex-shrink: 0;
      }

      .sort-menu {
        --boxel-menu-item-content-padding: var(--boxel-sp-xs);
        width: 190px;
      }
    </style>
  </template>

  private get sortOptions() {
    return this.args.options.map((option) => {
      return new MenuItem({
        label: option.displayName,
        action: () => this.args.onSort(option),
        icon: option.sort?.[0].direction === 'desc' ? ArrowDown : ArrowUp,
        checked:
          option.displayName === this.args.selected.displayName &&
          option.sort?.[0].direction === this.args.selected.sort?.[0].direction,
      });
    });
  }
}

import GlimmerComponent from '@glimmer/component';
